angular.module('ecoposApp')
    .directive('scrollPages', function () {
        return {
            link: function (scope, element, attrs) {
                var offset = parseInt(attrs.threshold) || 0;
                var e = element[0];
                var nextPage = attrs.nextPage?attrs.nextPage:null;
                var prevPage = attrs.prevPage?attrs.prevPage:null;
                var lastTop = -1;

                element.bind('scroll', function () {
                    if(prevPage && e.scrollTop < lastTop && e.scrollTop <= offset){
                        if(scope.$apply(prevPage) && attrs.backScroll){
                            lastTop = -1;
                            e.scrollTop = e.scrollHeight-e.offsetHeight-offset-1;
                        }
                    }
                    if(nextPage && e.scrollTop + e.offsetHeight >= e.scrollHeight - offset) {
                        if(scope.$apply(nextPage) && attrs.backScroll){
                            e.scrollTop = offset+1;
                        }
                    }
                    lastTop = e.scrollTop;
                });
            }
        };
    }
);

angular.module('ecoposApp')
	.directive('datetimepicker', [
		function() {
			if (angular.version.full < '1.1.4') {
				return {
					restrict: 'EA',
					template: "<div class=\"alert alert-danger\">Angular 1.1.4 or above is required for datetimepicker to work correctly</div>"
				};
			}
			return {
				restrict: 'EA',
				require: 'ngModel',
				scope: {
					ngModel: '=',
					dayFormat: "=",
					monthFormat: "=",
					yearFormat: "=",
					dayHeaderFormat: "=",
					dayTitleFormat: "=",
					monthTitleFormat: "=",
					showWeeks: "=",
					startingDay: "=",
					yearRange: "=",
					dateFormat: "=",
					minDate: "=",
					maxDate: "=",
					dateOptions: "=",
					dateDisabled: "&",
					hourStep: "=",
					minuteStep: "=",
					showMeridian: "=",
					meredians: "=",
					mousewheel: "=",
					showMinutes: "="
				},
				template: function(elem, attrs) {
					function dashCase(name, separator) {
						return name.replace(/[A-Z]/g, function(letter, pos) {
							return (pos ? '-' : '') + letter.toLowerCase();
						});
					}

					function createAttr(innerAttr, dateTimeAttrOpt) {
						var dateTimeAttr = angular.isDefined(dateTimeAttrOpt) ? dateTimeAttrOpt : innerAttr;
						if (attrs[dateTimeAttr]) {
							return dashCase(innerAttr) + "=\"" + dateTimeAttr + "\" ";
						} else {
							return '';
						}
					}

					function createFuncAttr(innerAttr, funcArgs, dateTimeAttrOpt) {
						var dateTimeAttr = angular.isDefined(dateTimeAttrOpt) ? dateTimeAttrOpt : innerAttr;
						if (attrs[dateTimeAttr]) {
							return dashCase(innerAttr) + "=\"" + dateTimeAttr + "({" + funcArgs + "})\" ";
						} else {
							return '';
						}
					}

					function createRequiredAttr(innerAttr, dateTimeAttrOpt) {
						var dateTimeAttr = angular.isDefined(dateTimeAttrOpt) ? dateTimeAttrOpt : innerAttr;
						if (attrs[dateTimeAttr]) {
							return dashCase(innerAttr) + "=\"" + attrs[dateTimeAttr] + "\" ";
						} else {
							return dashCase(innerAttr);
						}
					}

					function createAttrConcat(previousAttrs, attr) {
						return previousAttrs + createAttr.apply(null, attr);
					}
					var tmpl = "<div class=\"datetimepicker-wrapper\">" +
						"<input type=\"text\" ng-model=\"ngModel\" " + [
						["min", "minDate"],
						["max", "maxDate"],
						["dayFormat"],
						["monthFormat"],
						["yearFormat"],
						["dayHeaderFormat"],
						["dayTitleFormat"],
						["monthTitleFormat"],
						["showWeeks"],
						["startingDay"],
						["yearRange"],
						["datepickerOptions", "dateOptions"]
					].reduce(createAttrConcat, '') +
						createFuncAttr("dateDisabled", "date: date, mode: mode") +
						createRequiredAttr("datepickerPopup", "dateFormat") +
						"/>\n" +
						"</div>\n" +
						"<div class=\"datetimepicker-wrapper\" ng-model=\"time\" ng-change=\"time_change()\" style=\"display:inline-block\">\n" +
						"<timepicker " + [
						["hourStep"],
						["minuteStep"],
						["showMeridian"],
						["meredians"],
						["mousewheel"]
					].reduce(createAttrConcat, '') +
						"></timepicker>\n" +
						"</div>";
					return tmpl;
				},
				controller: ['$scope',
					function($scope) {
						$scope.time_change = function() {

							if (angular.isDefined($scope.ngModel) && angular.isDefined($scope.time)) {
								$scope.ngModel.setHours($scope.time.getHours(), $scope.time.getMinutes());
							}
						};
					}
				],
				link: function(scope, element, $scope) {
					scope.$watch(function() {
						return scope.ngModel;
					}, function(ngModel) {
						scope.time = ngModel;
					});
					scope.$watch(function() {
						return scope.showMinutes;
					}, function(showMinutes) {
						// var tds = element.find('td').slice(1);
						// console.log(tds);

					});
				}
			};
		}
	]);

angular.module('ecoposApp').filter('ecoFilter', function() {
    function fieldMatches(value, target, operand){
        var result = false;
        switch(operand){
            case '=':
            case '==':
            case '===':
                result = (value === target);
                break;
            case '>':
                result = (value > target);
                break;
            case '>=':
                result = (value >= target);
                break;
            case '<':
                result = (value < target);
                break;
            case '<=':
                result = (value <= target);
                break;
            case 'contains':
                //contains: does value contain target? (value is array or string)
                if(!angular.isString(value) && !angular.isArray(value)){
                    if(angular.isObject(value)){
                        value = Object.keys(value);
                    }
                    else{
                        value = [value];
                    }
                }
                result = (value.indexOf(target) !== -1);
                break;
            case 'containedIn':
                //containedIn: is value found in target? (target is array or string)
                if(!angular.isString(target) && !angular.isArray(target)){
                    if(angular.isObject(target)){
                        target = Object.keys(target);
                    }
                    else{
                        target = [target];
                    }
                }
                result = (target.indexOf(value) !== -1);
                break;
            case 'containsAny':
                //containsAny: does value contain any of the items in target? (target is array, value is array or string)
                if(!angular.isArray(target)){
                    if(angular.isObject(target)){
                        target = Object.keys(target);
                    }
                    else{
                        target = [target];
                    }
                }
                if(!angular.isArray(value)){
                    if(!angular.isString(value) && angular.isObject(value)){
                        value = Object.keys(value);
                    }
                    else{
                        value = [value];
                    }
                }
                angular.forEach(value, function(cValue){
                    if(target.indexOf(cValue) !== -1){
                        result = true;
                    }
                });
                break;
        }

        return result;
    }

    function extractChild(base, child){
        var result = null;
        try{
            eval("result = base"+((child.charAt(0)!=='.' && child.charAt(0)!=='[')?'.':'')+child); // jshint ignore:line
        }
        catch(error){
            result = null;
        }
        return result;
    }

    return function(input,args,matchAll) {
        /**
         * when matchAll is true, all args must be true to pass (ie. "AND")
         * when matchAll is false, any args may be true to pass (ie. "OR")
         *
         * args is of format:
         * [{
         *      field: <fieldName>,
         *      match:  ('=','>','>=','<','<=','contains','containedIn','containsAny')
         *      value: <matchValue>,
         * }]
         *
         * contains: does value contain target? (value is array or string)
         * containedIn: is value found in target? (target is array or string)
         * containsAny: does value contain any of the items in target? (target is array, value is array or string)
         *
         * if match is an array of operands, value must also be an array and conditions will be applied to values sequentially
         *
         * ex. to match 'startDate' between date1 and date2:
         *  args = [
         *      {   field: 'startDate',
         *          match: ['>=','<=']
         *          value: [date1,date2]
         *      }
         *  ]
         *
         */
        var output = [];

        if(!args || !angular.isArray(args) || !args.length){
            output = input;
        }
        else{
            if(angular.isUndefined(matchAll)){
                matchAll = false;
            }
            if(!angular.isArray(args)){
                args = [args];
            }

            angular.forEach(input, function(item, key){
                var itemPass = matchAll; // if matchAll, true until not passing. if !matchAll, false until passing.

                angular.forEach(args, function(checkReq, checkIdx){
                    var reqPass = false;

                    var checkField;
                    if(checkReq.field.search(/[\.\[]/) >= 0){
                        checkField = extractChild(item, checkReq.field);
                    }
                    else{
                        checkField = item[checkReq.field];
                    }

                    if(checkField){
                        if(angular.isArray(checkReq.match)){
                            if(angular.isArray(checkReq.value) && checkReq.value.length >= checkReq.match.length){
                                var allPass = true;
                                angular.forEach(checkReq.match, function(cOp, cMatchIdx){
                                    if(!fieldMatches(checkField, checkReq.value[cMatchIdx], cOp)){
                                        allPass = false;
                                    }
                                });
                                reqPass = allPass;
                            }
                            else{
                                // not enough values given for the requested match operands
                                reqPass = false;
                            }
                        }
                        else{
                            reqPass = fieldMatches(checkField, checkReq.value, checkReq.match);
                        }
                    }

                    if(matchAll && !reqPass){
                        itemPass = false;
                    }
                    else if(!matchAll && reqPass){
                        itemPass = true;
                    }
                });

                if(itemPass){
                    output.push(item);
                }
            });
        }

        return output;
    };
});
angular.module('ecoposApp').filter('bulkPrice', function($log) {
	return function(fromPrice,fromUnit,fromSize,toUnit,toSize) {
        if(typeof fromUnit === 'undefined'){
            fromUnit = 'lb';
        }
        if(typeof fromSize === 'undefined'){
            fromSize = 1;
        }
        if(typeof toUnit === 'undefined'){
            toUnit = 'g';
        }
        if(typeof toSize === 'undefined'){
            toSize = 100;
        }

        var conversions = {
            'g-g': 1.0,
            'lb-lb': 1.0,
            'lb-g': 453.592,
            'g-lb': 1.0/453.592
        };

        var toPrice = fromPrice;

        if(conversions[fromUnit+'-'+toUnit]){
            toPrice = (fromPrice/fromSize / conversions[fromUnit+'-'+toUnit])*toSize;
        }
        else{
            $log.error('unknown conversion \''+fromUnit+'->'+toUnit+'\'');
        }

		return toPrice;
	};
});
angular.module('ecoposApp').filter('eventFilter', function() {
	return function(input,args) {
        var output = {};
        angular.forEach(input, function(event, key){
            if(args.isCalendar && event.date){
                output[key] = event;
            }
            else if(args.isTodo && event.type === 'todo'){
                output[key] = event;
            }
        });
		return output;
	};
});
